<script setup>
import { ref, computed, reactive } from 'vue'
import count from './count.vue'
import reactived from './react.vue'
import clickCycle from './clickCycle.vue'
import NotFound from './NotFound.vue'
import modal from './modal.vue'
import Bouncy from './bouncy.vue'
import watcha from './watch.vue'
import SimpleVmodel from './simpleVmodel.vue'
import vforif from './vforif.vue'
import vforList from './vforList.vue'
import immutable from './immutable.vue'
import vforComponent from './vforComponent.vue'
import vforObject from './vforObject.vue'
import vmodelModifier from './vmodelModifier.vue'
import boredum from './boredum.vue'
const routes = {
  '/' : SimpleVmodel,
  '/NotFound': NotFound,
  '/reactive': reactived,
  '/modal' : modal,
  '/clickCycle' : clickCycle,
  '/bouncy': Bouncy,
  '/watcha' : watcha,
  '/vforif' : vforif,
  '/vforList' : vforList,
  '/count' : count,
  '/immutable' : immutable,
  '/vforObject' : vforObject,
  '/vforComponent' : vforComponent,
  '/vmodelModifier' : vmodelModifier,
  '/boredum' : boredum,
}
const currentPath = ref(window.location.hash)
window.addEventListener('hashchange', () => {
  currentPath.value = window.location.hash
})
const currentView = computed(() => {
  return routes[currentPath.value.slice(1) || '/'] || NotFound
})
</script>

<template>
